/*********************************************************************************************
Created by Ashok Kumar - 2nd June, 2014 -  Function that returns list of States in the Form of a Table based
on the StatesIds given as input in the String format with comma seperator
*********************************************************************************************/
/* Modification History */
/* */
CREATE FUNCTION [dbo].[SplitString]
(
 @String varchar(8000),
 @Delimiter char(1)
)
 returns
 @temptable TABLE (items varchar(100))
AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION
      DECLARE @address_idendity BIGINT

    IF @address_id=0
        BEGIN
           INSERT INTO [dbo].[address]
           (
                        [personal_info_id],
                        [street_address],
                        [city],
                        [personal_address_state_id],
                        [foreign_state],
                        [country_id],
                        [zip_code],
                        [phone],
                        [address_type],
                        [residence_state_id],
                        [has_lived_in_more_than_one_state],
                        [has_earned_income_from_non_lived_state],
                        [lived_in_another_state_id],
                        [earned_income_from_non_lived_state_id],
                        [apartment_number],
                        [postal_code],
                        [update_datetime]             
               )
               VALUES
               (
                        @personal_info_id,
                        @street_address,
                        @city,
                        @personal_address_state_id,
                        @foreign_state,
                        @country_id,
                        @zip_code,
                        @phone,
                        @address_type,
                        @residence_state_id,
                        @has_lived_in_more_than_one_state,
                        @has_earned_income_from_non_lived_state,
                        @lived_in_another_state_id,
                        @earned_income_from_non_lived_state_id,
                        @apartment_number,
                        @postal_code,
                        GETDATE()
                  )
            SET @address_idendity=@@IDENTITY

            INSERT INTO [dbo].[address_history]
            (
                        [address_id],
                        [street_address],
                        [city],
                        [personal_address_state_id],
                        [foreign_state],
                        [country_id],
                        [zip_code],
                        [phone],
                        [address_type],
                        [residence_state_id],
                        [has_lived_in_more_than_one_state],
                        [has_earned_income_from_non_lived_state],
                        [lived_in_another_state_id],
                        [earned_income_from_non_lived_state_id],
                        [apartment_number],
                        [postal_code]
                  )
                  VALUES
                  (
                        @address_idendity,
                        @street_address,
                        @city,
                        @personal_address_state_id,
                        @foreign_state,
                        @country_id,
                        @zip_code,
                        @phone,
                        @address_type,
                        @residence_state_id,
                        @has_lived_in_more_than_one_state,
                        @has_earned_income_from_non_lived_state,
                        @lived_in_another_state_id,
                        @earned_income_from_non_lived_state_id,
                        @apartment_number,
                        @postal_code
                  )
       END
     ELSE IF( @address_id<>0)
            BEGIN 

            DECLARE @modified as int  = 0

            SET @modified = (SELECT COUNT(*) FROM (
                                                SELECT [personal_info_id],[street_address],[city],[personal_address_state_id],[foreign_state],
                                                            [country_id],[zip_code],[phone],[address_type],[residence_state_id],[has_lived_in_more_than_one_state],
                                                            [lived_in_another_state_id],[earned_income_from_non_lived_state_id],[apartment_number],[postal_code]
                                                FROM [dbo].[address] 
                                                WHERE [address_id]=@address_id
                                                UNION
                                                SELECT @personal_info_id,@street_address,@city,@personal_address_state_id,@foreign_state,
                                                            @country_id,@zip_code,@phone,@address_type,@residence_state_id,@has_lived_in_more_than_one_state,
                                                            @lived_in_another_state_id,@earned_income_from_non_lived_state_id,@apartment_number,@postal_code) is_modified)

            IF(@modified > 1)
            BEGIN
                  UPDATE  [dbo].[address]
                        SET         [personal_info_id]=@personal_info_id,
                                    [street_address]=@street_address,
                                    [city]=@city,
                                    [personal_address_state_id]=@personal_address_state_id,
                                    [foreign_state]=@foreign_state,
                                    [country_id]=@country_id,
                                    [zip_code]=@zip_code,
                                    [phone]=@phone,
                                    [address_type]    =@address_type,
                                    [residence_state_id]=@residence_state_id,
                                    [has_lived_in_more_than_one_state]=@has_lived_in_more_than_one_state,
                                    [has_earned_income_from_non_lived_state]=@has_earned_income_from_non_lived_state,
                                    [lived_in_another_state_id]=@lived_in_another_state_id,
                                    [earned_income_from_non_lived_state_id]=@earned_income_from_non_lived_state_id,
                                    [apartment_number]=@apartment_number,
                                    [postal_code]=@postal_code,
                                    [update_datetime]=GETDATE()   
                        WHERE [address_id]=@address_id

                  INSERT INTO [dbo].[address_history]
                        (
                              [address_id],
                              [street_address],
                              [city],
                              [personal_address_state_id],
                              [foreign_state],
                              [country_id],
                              [zip_code],
                              [phone],
                              [address_type],
                              [residence_state_id],
                              [has_lived_in_more_than_one_state],
                              [has_earned_income_from_non_lived_state],
                              [lived_in_another_state_id],
                              [earned_income_from_non_lived_state_id],
                              [apartment_number],
                              [postal_code]
                        )
                        VALUES
                        (
                              @address_id,
                              @street_address,
                              @city,
                              @personal_address_state_id,
                              @foreign_state,
                              @country_id,
                              @zip_code,
                              @phone,
                              @address_type,
                              @residence_state_id,
                              @has_lived_in_more_than_one_state,
                              @has_earned_income_from_non_lived_state,
                              @lived_in_another_state_id,
                              @earned_income_from_non_lived_state_id,
                              @apartment_number,
                              @postal_code
                        )
            END
            SET @address_idendity=@address_id   
      END

         SELECT @address_idendity AS 'address_id'

      IF @@ERROR = 0
        COMMIT TRANSACTION
      ELSE
        ROLLBACK TRANSACTION

      SET NOCOUNT OFF;
END

