CREATE PROCEDURE [dbo].[usp_get_ez_eligibility_by_personal_info_id]
@personal_info_id [BIGINT]
AS

BEGIN
SET NOCOUNT ON;
SELECT [question_id], [question] FROM [dbo].[security_question] WITH (NOLOCK) WHERE [is_deleted] = CAST(0 AS BIT)
SELECT security_question_id AS selected_question_id FROM [dbo].[user] WITH (NOLOCK) WHERE [user_id] = @user_id
SET NOCOUNT OFF;
END

BEGIN SET NOCOUNT ON;
SELECT q.[question_id], q.[question] FROM [dbo].[user] AS u WITH(NOLOCK) INNER JOIN [dbo].[security_question] AS q WITH(NOLOCK) ON u.[security_question_id] = q.[question_id] WHERE u.[user_id] = @user_id
SET NOCOUNT OFF;
END

BEGIN SET NOCOUNT ON;
IF EXISTS(SELECT 1 FROM [dbo].[user] WITH(NOLOCK) WHERE [username]=@username AND [is_active] = 1 AND [is_deleted] = 0)
BEGIN SELECT [user_id], [password], [salt] FROM [dbo].[user] WITH(NOLOCK) WHERE [username]=@username AND [is_active] = 1 AND [is_deleted] = 0 END SET NOCOUNT OFF; END

/*****************************************************************************************
Created by Thileepkumar 26 April 2014 -  Procedure to validate User Password and get User Details by User Id
******************************************************************************************/
CREATE PROCEDURE [dbo].[usp_get_user_authentication_by_user_id]  
(
@user_id bigint
)
AS

BEGIN SET NOCOUNT ON;
SELECT u.[user_id] FROM [dbo].[user] u WITH(NOLOCK) INNER JOIN [dbo].[personal_info] p WITH(NOLOCK) ON u.[user_id]=p.[user_id] WHERE p.[first_name]=@first_name AND p.[last_name]=@last_name AND CAST(CONVERT(VARCHAR(10),p.[date_of_birth],110)AS DATETIME)=CAST(CONVERT(VARCHAR(10),@date_of_birth,110)AS DATETIME) AND p.[social_security_number]=@social_security_number AND u.[is_active] = 1 AND u.[is_deleted] = 0 AND p.[is_deleted] = 0
SET NOCOUNT OFF; END

/*10 Mar 2014 Bala Retrieve Userid given by user & personal_info details
* Modified by Sathish 19Mar2014 removed security question and security answer
Modified by Sathish 25Mar2014 changed social security number datatype to varchar
*/

CREATE PROCEDURE [dbo].[usp_get_user_id_by_personal_details]
@first_name NVARCHAR(100),
@last_name NVARCHAR(100),
@date_of_birth DATETIME,
@social_security_number VARCHAR(15)
AS

BEGIN SET NOCOUNT ON;
SELECT [username] FROM [dbo].[user] WITH(NOLOCK) WHERE ([email]=@email AND [is_active] = 1 AND [is_deleted] = 0)
SET NOCOUNT OFF; END

BEGIN SET NOCOUNT ON;
SELECT u.[user_id], u.[username] FROM [dbo].[user] u WITH(NOLOCK) INNER JOIN [dbo].[personal_info] p WITH(NOLOCK) ON u.[user_id]=p.[user_id] WHERE (p.[first_name]=@first_name) AND (p.[last_name]=@last_name) AND (CAST(CONVERT(VARCHAR(10),p.[date_of_birth],110)AS DATETIME)=CAST(CONVERT(VARCHAR(10),@date_of_birth,110)AS DATETIME)) AND (u.[email]=@email) AND (u.[is_active] = 1) AND (u.[is_deleted] = 0) AND (p.[is_deleted] = 0)
SET NOCOUNT OFF; END

