/*******************************************************************************
Created by Balasubramanian - 2 April 2014 - Procedure for get user_data_id column from [user_data] table by user_id column
/* Modification History*/ 
********************************************************************************/   
CREATE PROCEDURE [dbo].[usp_get_user_data_id_by_user_id]
@user_id BIGINT
AS

BEGIN

SET NOCOUNT ON;
BEGIN TRANSACTION
DECLARE @order_identity BIGINT

         INSERT INTO [dbo].[order]
         ( 
				[user_data_id],
				[shopping_cart_id],
				[order_amount],
				[card_number] ,
				[cardtype_id] ,
				[card_expires],
				[processed] ,
				[time] ,
				[o_authorization] ,
				[ip_address] ,
				[is_deleted] ,
				[create_timestamp],
				[update_timestamp]
          )

		  VALUES
          (
				@user_data_id,
				@shopping_cart_id ,
				@order_amount ,
				@card_number ,
				@cardtype_id ,
				@card_expires ,
				@processed ,
				@time ,
				@o_authorization ,
				@ip_address ,
				0,
				GETDATE(),
				GETDATE()
          )

	SET @order_identity=@@IDENTITY   

            INSERT INTO [dbo].[order_billing_address]
            (
                [order_id] ,
				[name] ,
				[email] ,
				[street_address] ,
				[apartment_number],
				[city] ,
				[state_name] ,
				[country_name] ,
				[phone] ,
				[postcode] ,
				[is_deleted] ,
				[create_datetime] ,
				[update_datetime] 
            )
            VALUES
            (           
				@order_identity,
				@name ,
				@email,
				@street_address,
				@apartment_number,
				@city ,
				@state_name,
				@country_name ,
				@phone ,
				@postcode,
				0,
				GETDATE(),
				GETDATE()
            )

			 UPDATE [dbo].[order_detail_mapper] SET [is_deleted] = 1
			 WHERE [shopping_cart_id] = @shopping_cart_id

			 UPDATE [dbo].[shopping_cart] SET [is_deleted] = 1
			 WHERE [shopping_cart_id] = @shopping_cart_id

			 SELECT @order_identity

      IF @@ERROR = 0
        COMMIT TRANSACTION
      ELSE
        ROLLBACK TRANSACTION

SET NOCOUNT OFF;
END

